<h3>gulp.js</h3>
<pre class="lang:js decode:true " title="gulp.js">//gulp.〇〇の処理は全部定数でまとめる
const {
  src,
  dest,
  watch,
  lastRun,
  series,
  parallel
} = require("gulp");

// html
const htmlMin = require("gulp-htmlmin");

// Sass
const sass = require("gulp-dart-sass");
const notify = require("gulp-notify");
const plumber = require("gulp-plumber");
const postCss = require("gulp-postcss"); //for autoprefixer
const autoprefixer = require("autoprefixer");
const gcmq = require("gulp-group-css-media-queries");

// JavaScript
const babel = require("gulp-babel");
&lt;span class="strike"&gt;const uglify = require("gulp-uglify");
&lt;/span&gt;const terser = require("gulp-terser");//ES6の圧縮用に追加

// 画像圧縮
const imagemin = require("gulp-imagemin");
const imageminMozjpeg = require("imagemin-mozjpeg");
const imageminPngquant = require("imagemin-pngquant");
const imageminGifsicle = require("imagemin-gifsicle");
const imageminSvgo = require("imagemin-svgo");

// ブラウザ同期
const browserSync = require("browser-sync").create();

//パス設定
const paths = {
  html: {
    src: "./src/*.html",
    dist: "./dist/",
  },
  styles: {
    src: "./src/scss/**/*.scss",
    dist: "./dist/css/",
    map: "./map",
  },
  scripts: {
    src: "./src/js/**/*.js",
    dist: "./dist/js/",
  },
  images: {
    src: "./src/img/**/*.{jpg,jpeg,png,gif,svg}",
    dist: "./dist/img/",
  },
};

// htmlフォーマット
const htmlFormat = () =&gt; {
  return src(paths.html.src)
    .pipe(
      plumber({
        //エラーがあっても処理を止めない
        errorHandler: notify.onError("Error: &lt;%= error.message %&gt;"),
      })
    )
    .pipe(
      htmlMin({
        //HTMLの圧縮
        minifyCSS: true, //style要素とstyle属性の圧縮
        minifyJS: true, //js要素とjs属性の圧縮
        removeComments: true, //コメントを削除
        collapseWhitespace: true, //余白を詰める
        collapseInlineTagWhitespace: true, //inline要素間のスペース削除（spanタグ同士の改行などを詰める
        preserveLineBreaks: true, //タグ間の改行を詰める
        &lt;del&gt;removeEmptyAttributes: false //空属性を削除しない&lt;/del&gt;（デフォルトでfalseなので削除
      })
    )
    .pipe(dest(paths.html.dist));
};

// Sassコンパイル
const sassCompile = () =&gt; {
  return src(paths.styles.src, {
      sourcemaps: true,
    })
    .pipe(
      plumber({
        errorHandler: notify.onError("Error: &lt;%= error.message %&gt;"),
      })
    )
    .pipe(
      sass({
        outputStyle: "expanded",
      }).on("error", sass.logError)
    )
    .pipe(
      postCss([
        autoprefixer({
          // プロパティのインデントを整形しない
          cascade: false,
          // IE11のgrid対応
          grid: "autoplace",
        }),
      ])
    )
    //メディアクエリをまとめる
    .pipe(gcmq())
    .pipe(
      dest(paths.styles.dist, {
        sourcemaps: "./map",
      })
    )
    // 変更があったらリロードせずにCSSのみ更新
    .pipe(browserSync.stream())
};

// JavaScriptコンパイル
const jsBabel = () =&gt; {
  return src(paths.scripts.src)
    .pipe(
      plumber({
        errorHandler: notify.onError("Error: &lt;%= error.message %&gt;"),
      })
    )
    .pipe(
      // Babel変換
      babel({
        presets: ["@babel/preset-env"],
      })
    )
    .pipe(dest(paths.scripts.dist))
    // JS圧縮
    &lt;span class="strike"&gt;.pipe(uglify())&lt;/span&gt;
    .pipe(terser())
    .pipe(dest(paths.scripts.dist));
};

// 画像圧縮
const imagesCompress = () =&gt; {
  return src(paths.images.src, {
      // 更新があった場合に処理
      since: lastRun(imagesCompress),
    })
    .pipe(
      plumber({
        errorHandler: notify.onError("Error: &lt;%= error.message %&gt;"),
      })
    )
    .pipe(
      imagemin(
        [
          // JPG
          imageminMozjpeg({
            quality: 80,
          }),
          // PNG
          imageminPngquant(
            [0.7, 0.8] //画質の最小,最大
          ),
          // GIF
          imageminGifsicle(),
          // SVG
          imageminSvgo({
            plugins: [
          {
            removeViewbox: false, //フォトショやイラレで書きだされるviewboxを消すかどうか※表示崩れの原因になるのでfalse推奨。以降はお好みで。
          },
          {
            removeMetadata: false, //&lt;metadata&gt;を削除するかどうか
          },
          {
            convertColors: false, //rgbをhexに変換、または#ffffffを#fffに変換するかどうか
          },
          {
            removeUnknownsAndDefaults: false, //不明なコンテンツや属性を削除するかどうか→アニメーションに影響するのでfalse
          },
          {
            convertShapeToPath: false, //コードが短くなる場合だけ&lt;path&gt;に変換するかどうか→アニメーションに影響するのでfalse
          },
          { collapseGroups: false }, // 重複や不要な`&lt;g&gt;`タグを削除する→アニメーションに影響するのでfalse
          {
            cleanupIDs: false, //SVG内に&lt;style&gt;や&lt;script&gt;がなければidを削除するかどうか
          },
        ],
          }),
        ], {
          //ターミナルへの情報出力非表示
          verbose: true,
        }
      )
    )
    .pipe(dest(paths.images.dist));
};

// ローカルサーバー起動
const browserSyncFunc = (done) =&gt; {
  browserSync.init({
    //デフォルトのconnectedのメッセージ非表示
    notify: false,
    server: {
      baseDir: "./",
    },
    startPath: "./dist/index.html",
    reloadOnRestart: true,
  });
  done();
};

// ブラウザ自動リロード
const browserReloadFunc = (done) =&gt; {
  browserSync.reload();
  done();
};

// ファイル監視
const watchFiles = () =&gt; {
  watch(paths.html.src, series(htmlFormat, browserReloadFunc));
  watch(paths.styles.src, series(sassCompile));
  watch(paths.scripts.src, series(jsBabel, browserReloadFunc));
  watch(paths.images.src, series(imagesCompress, browserReloadFunc));
};

// npx gulp実行処理
exports.default = series(
  parallel(htmlFormat, sassCompile, jsBabel, imagesCompress),
  parallel(watchFiles, browserSyncFunc)
);</pre>
<h3>browserslistはpackage.jsonへ</h3>
時々<span class="marker"><strong>gulp.jsに書いてある記事を見かけますが、非推奨なのでpackage.jsonに書きましょう。</strong></span>
<div class="memo-box common-icon-box"><strong>【2022年2月追記】</strong>
<strong>前回紹介していた書き方でnot deadを指定するとエラーになります。</strong>
基本的には「defaults＋必要な条件」で追記するのが良さそうです。詳細は公式ページでご確認ください。↓
<a href="https://github.com/browserslist/browserslist" target="_blank" rel="noopener">https://github.com/browserslist/browserslist</a>

なおdefaultsに設定されている条件は
<strong>'&gt; 1%',</strong>
<strong>'last 2 versions',</strong>
<strong>'Firefox EST',
'not dead'</strong>
となっております。</div>
（例）
<pre class="lang:default decode:true" title="package.json">"devDependencies": {
	～（中略）～
},
"browserslist": [
	<del>"last 3 version",
	"IE &gt;= 10",
	"&gt; 1% in JP",
	"not dead"</del>（デフォルトにnot deadが定義されているためエラーが出るので削除）
  "defaults",
  "safari &gt;= 13"//あくまで例です
],</pre>
<h3>必要なパッケージをインストールする</h3>
下記コマンドをターミナルなどで叩けば必要なものをインストールできます。（バージョンはすべて2021年3月時点の最新版で検証したところ、エラーは出ておりません。）
<pre class="lang:default decode:true " title="コマンド">npm i -D @babel/core @babel/preset-env autoprefixer browser-sync browserslist gulp  gulp-babel gulp-dart-sass gulp-group-css-media-queries gulp-htmlmin gulp-imagemin gulp-notify gulp-plumber gulp-postcss gulp-uglify imagemin-gifsicle imagemin-mozjpeg imagemin-pngquant imagemin-svgo postcss</pre>
